import { useQuery } from "@tanstack/react-query";
import axios from "axios";

const getUrl = (key) => `/json/${key}.json`;

const queries = {
  employees: {
    queryFn: async () => {
      const url = getUrl("employees");
      try {
        const response = await axios.get(url);
        return response.data.map((item) => {
            const newAddress = Object.values(item.address).join(' ,')
            console.log(newAddress)
            return { ...item, address: newAddress, id: item.employeeID }  
        })
      } catch(e) {
        console.log('Caught error: ', e)
      }

    },
    onSuccess: (data) => {
        return data.data.map((item) => {
            const newAddress = Object.values(item.address).join(' ,')
            console.log(newAddress)
            return { ...item, address: newAddress, id: item.employeeID }  
        })
    }
  },
  orders: {
    queryFn: () => {
      const url = getUrl("orders");
      return axios.get(url);
    },
  },
};

const useFavourites = (key) => {
  const { queryFn, onSuccess } = (queries[key] || {});
  return useQuery({ queryFn, queryKey: [`FAVOURITES-${key}`], enabled: !!key, onSuccess });
};

export default useFavourites;
