import { memo, forwardRef, useState, useMemo, useCallback } from "react";
import { TableVirtuoso } from "react-virtuoso";

import MaterialTable from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";

const VirtuosoTableComponents = {
  Scroller: forwardRef((props, ref) => (
    <TableContainer component={Paper} {...props} ref={ref} />
  )),
  Table: (props) => (
    <MaterialTable
      {...props}
      sx={{ borderCollapse: "separate", tableLayout: "fixed" }}
    />
  ),
  TableHead,
  TableRow: ({ item: _item, ...props }) => <TableRow {...props} />,
  TableBody: forwardRef((props, ref) => <TableBody {...props} ref={ref} />),
};

function fixedHeaderComponent(headerRow) {
  return (
    <TableRow>
      {Object.keys(headerRow)
        .filter((key) => key !== "id")
        .map((key) => (
          <TableCell key={key} style={{ textTransform: "capitalize" }}>
            {key}
          </TableCell>
        ))}
    </TableRow>
  );
}

function rowContent(index, row) {
  const values = Object.entries(row);
  return values
    .filter((entry) => entry[0] !== "id")
    .map((entry, index) => (
      <TableCell key={`${entry[1]}-${index}`}>{entry[1].toString()}</TableCell>
    ));
}

