import { useCallback, useState } from "react";
import useSubmitQuery from "../queries/useSubmitQuery";
import { useMutation, useQuery } from "@tanstack/react-query";
import Table from "../components/Table";

import styles from "./SqlQueryView.module.scss";
import { Divider } from "@mui/material";
import Dropdown from "../components/Dropdown/Dropdown";

const SqlQueryView = () => {
  const [query, setQuery] = useState("");
  const { mutate, data } = useSubmitQuery();
  const [selectedFavourite, setSelectedFavourite] = useState(null);

  const favouriteSelectHandler = useCallback((value) => setSelectedFavourite(value));

  const { data: tableData } = useSubmitQuery(selectedFavourite);


  return (
    <div style={{ display: "flex" }}>
      <div className={styles.container}>
        <textarea onChange={(e) => setQuery(e.target.value)} value={query} />
        <div className={styles.btnGrp}>
          <button onClick={() => setQuery("")}>Clear</button>
          <button onClick={() => mutate()}>Submit</button>
        </div>
      </div>
      <Divider />
      { tableData || data && <Table data={tableData || data} />}
    </div>
  );
};

export default SqlQueryView;
