import { useState } from "react";
import useSubmitQuery from "../queries/useSubmitQuery";

import styles from "./SqlQueryView.module.scss";

const SqlQueryView = () => {
  const [query, setQuery] = useState("");
  const { mutate: submitQuery} = useSubmitQuery();

  return (
    <div className={styles.container}>
      <textarea onChange={(e) => setQuery(e.target.value)}>{query}</textarea>
      <div className={styles.btnGrp}>
        <button onClick={() => setQuery("")}>Clear</button>
        <button onClick={submitQuery}>Submit</button>
      </div>
    </div>
  );
};

export default SqlQueryView;
