import { useQuery } from "@tanstack/react-query";
import axios from "axios";

const getUrl = (key) => `/json/${key}.json`;

const queries = {
  employees: {
    queryFn: () => {
      const url = getUrl("employees");
      return axios.get(url);
    },
    onSucess: (data) => {
        return data.map((item) => {
            const newAddress = Object.values(item.address).join(' ,')
            return ({ ...item, address: newAddress })    
        })
    }
  },
  orders: {
    queryFn: () => {
      const url = getUrl("employees");
      return axios.get(url);
    },
  },
};

const useFavourites = (key) => {
  const { queryFn, onSucess } = (queries[key] || {});
  return useQuery({ queryFn, queryKey: [`FAVOURITES-${key}`], enabled: !!key, onSucess });
};

export default useFavourites;
